create table pizza_selling_per_day (
  date DATE PRIMARY KEY,
  pepperoni INT4,
  branco INT4,
  all_dressed INT4
);

CREATE TABLE public.prices (
	flavor varchar NULL,
	price float4 NULL
);


CREATE TABLE public.receipts (
	flavor varchar NULL,
	pepperoni int4 NULL,
	cheese int4 NULL,
	vedgetable int4 NULL,
	dough int4 NULL,
	sauce int4 NULL
);


CREATE TABLE public.ingredients (
	ingredient varchar NULL,
	price float4 NULL
);

